import React from 'react';
import PropTypes from 'prop-types';
import styled from 'styled-components';
import './alert.css';

const Wrapper = styled.div`
	display: flex;
	flex-direction: column;
	margin-top: 10px;
`;

export default function Alert(props) {
	const { alerts } = props;
	if (!alerts) return null;
	const weatherAlerts = [];

	for (let i = 0; i < alerts.length; i++) {
		let others = alerts[i].uri
			? { url: alerts[i].uri, clickable: true }
			: { clickable: false };
		weatherAlerts.push(
			<SingleAlert
				key={`weather-alert-${i}`}
				title={alerts[i].title}
				{...others}
			/>
		);
	}
	return <Wrapper>{weatherAlerts}</Wrapper>;
}

function SingleAlert(props) {
	return props.clickable ? (
		<a
			className="alert-warning"
			href={props.uri}
			target="_blank"
			rel="noopener noreferrer"
		>
			{props.title}
		</a>
	) : (
		<span className="alert-warning">{props.title}</span>
	);
}

SingleAlert.propTypes = {
	title: PropTypes.string.isRequired,
	clickable: PropTypes.bool.isRequired,
	url: PropTypes.string
};

SingleAlert.defaultProps = {
	url: ''
};
