import React, { useState } from 'react';
import PropTypes from 'prop-types';
import styled from 'styled-components';
import './SearchForm.css';
import Search from '../icons/Search';

const Form = styled.form`
	display: flex;
	flex-direction: flex-row;
	border: ${props => (props.focused ? '1px black' : '1px #757578')} solid;
	border-radius: 10px;
	margin: 0px 5px 0px 5px;
	overflow: hidden;
	padding-left: 10px;
`;
const Span = styled.span`
	font-size: 12px;
	font-weight: ${props => (props.focused ? '500' : '400')};
	color: ${props => (props.focused ? 'black' : '#757578')};
	min-height: 15px;
	text-align: left;
	padding: 0px 42px 0px 11px;
	margin: 0px 5px 0px 5px;
`;

export default function SearchForm(props) {
	const [focused, setFocused] = useState(false);

	return (
		<div className="location-form-wrapper">
			<Span focused={focused}>
				{props.value.length > 0 && props.placeholder}
			</Span>
			<Form
				focused={focused}
				onFocus={() => setFocused(true)}
				onBlur={() => setFocused(false)}
				onSubmit={props.onSubmit}
			>
				<input
					className="location-input"
					type="text"
					value={props.value}
					onChange={props.onChange}
					placeholder={props.placeholder}
				/>
				<label className="location-form-submit">
					<Search color="white" />
				</label>
			</Form>
		</div>
	);
}

SearchForm.propTypes = {
	value: PropTypes.string.isRequired,
	placeholder: PropTypes.string,
	onChange: PropTypes.func.isRequired,
	onSubmit: PropTypes.func.isRequired
};

SearchForm.defaultProps = {
	placeholder: ''
};
