import React from 'react';
import PropTypes from 'prop-types';
import styled from 'styled-components';
import CurrentCard from './currentcard';

const Wrapper = styled.div`
	display: flex;
	flex-direction: row;
	justify-content: center;
	align-items: center;
	text-align: center;
`;

export default function WeatherView(props) {
	const data = props.data;

	function convertUnixToDate(unixTime) {
		const date = new Date(unixTime * 1000);
		return date.toLocaleDateString();
	}

	return Object.keys(data).length > 0 ? (
		<Wrapper>
			<span>{`Currently it is ${convertUnixToDate(data.currently.time)}`}</span>
			<span>{`The temperature is ${data.currently.temperature}`}</span>
			<CurrentCard />
		</Wrapper>
	) : (
		<Wrapper> No Data </Wrapper>
	);
}

WeatherView.propTypes = {
	data: PropTypes.object.isRequired
};
