import React, {useState} from "react"
import PropTypes from "prop-types"
import styled from "styled-components"

const Div = styled.div`
    display: flex;
    flex-direction: column;
`
const Input = styled.input`
    font-size: 16px;
    border: none;
    width: 100%;
    &:focus {
        border: none;
        outline: none;
    }
    &:-ms-clear {
        width: 0px;
        height: 0px;
    }
`
const Form = styled.form`
    display: flex;
    flex-direction: flex-row;
    border: ${props => props.focused ? "2px black" : "1px #757578"} solid;
    border-radius: 10px;
    padding: 10px;
    margin: 0px 5px 0px 5px;
`
const Span = styled.span`
    font-size: 10px;
    font-weight: ${props => props.focused ? "700" : "400"};
    color: ${props => props.focused ? "black" : "#757578"};
    min-height: 10px;
    text-align: left;
    padding: 0px 5px 0px 5px;
`

export default function SearchForm(props) {
    const [focused, setFocused] = useState(false)

    return (
        <Div>
            <Span>{props.value.length > 0 && props.placeholder }</Span>
            <Form focused={focused} onFocus={() => setFocused(true)} onBlur={() => setFocused(false)}>
                <Input type="text" value={props.value} onChange={props.onChange}/>
            </Form>
        </Div>
    )
}

SearchForm.propTypes = {
    value: PropTypes.string.isRequired,
    placeholder: PropTypes.string,
    onChange: PropTypes.func.isRequired,
}

SearchForm.defaultProps = {
    placeholder: ""
}
