import React from 'react';
import PropTypes from 'prop-types';
import styled from 'styled-components';

const Wrapper = styled.div`
	display: flex;
	flex-direction: column;
	margin-top: 10px;
`;

const Link = styled.a`
	font-size: 12pt;
	text-align: center;
	color: #ff1744;
	text-decorator: none;
	cursor: ${props => (props.clickable ? 'pointer' : 'text')};
`;

export default function Alert(props) {
	const { alerts } = props;
	if (!alerts) return null;
	const weatherAlerts = [];

	for (let i = 0; i < alerts.length; i++) {
		let others = alerts[i].uri
			? { url: alerts[i].uri, clickable: true }
			: { clickable: false };
		weatherAlerts.push(
			<SingleAlert
				key={`weather-alert-${i}`}
				title={alerts[i].title}
				{...others}
			/>
		);
	}
	return <Wrapper>{weatherAlerts}</Wrapper>;
}

function SingleAlert(props) {
	return (
		<Link clickable={props.clickable} href={props.uri}>
			{props.title}
		</Link>
	);
}

SingleAlert.propTypes = {
	title: PropTypes.string.isRequired,
	clickable: PropTypes.bool.isRequired,
	url: PropTypes.string
};

SingleAlert.defaultProps = {
	url: ''
};
