import React, { Component } from 'react';
import PropTypes from 'prop-types';
import axios from 'axios';
import SearchForm from './SearchForm';
import MyLocation from '../icons/MyLocation';
import './Location.css';

export default class Location extends Component {
	constructor(props) {
		super(props);
		this.state = {
			address: props.address,
			detectingLocation: false
		};
	}

	handleGeoCodeCall = url => {
		axios
			.post(`${url}&key=${process.env.REACT_APP_GOOGLEAPIKEY}`)
			.then(response => {
				if (response.data.status === 'OK') {
					const data = response.data.results[0];
					this.setState({ address: data.formatted_address }, () => {
						this.props.setCoordinates(data.geometry.location);
					});
				} else {
					this.props.onError();
					return;
				}
			})
			.catch(error => {
				this.props.onError();
			});
	};

	handleDetectLocation = () => {
		if (!navigator.geolocation) this.props.onError();
		else {
			const fallback = this.state.address;
			this.setState({ detectingLocation: true }, () => {
				navigator.geolocation.getCurrentPosition(
					this.setCurrentPosition,
					error => {
						this.handleDetection(fallback, error);
					}
				);
			});
		}
	};

	handleDetection = (fallback, error) => {
		this.setState({ address: fallback, detectingLocation: false }, () => {
			this.props.onError(error);
		});
	};

	setCurrentPosition = position => {
		const coordinates = {
			lat: position.coords.latitude,
			lng: position.coords.longitude
		};
		this.handleGeoCodeCall(
			`https://maps.googleapis.com/maps/api/geocode/json?latlng=${
				coordinates.lat
			},${coordinates.lng}`
		);
	};

	onChange = e => {
		if (this.state.detectingLocation) return;
		this.setState({ address: e.target.value });
	};

	onClear = e => {
		e.preventDefault();
		if (this.state.detectingLocation) return;
		this.setState({ address: '' });
	};

	onSubmit = e => {
		e.preventDefault();
		if (this.state.detectingLocation) return;
		this.handleGeoCodeCall(
			`https://maps.googleapis.com/maps/api/geocode/json?address=${
				this.state.address
			}`
		);
	};

	render() {
		const { address, detectingLocation } = this.state;
		return (
			<div className="location-form-wrapper">
				<div className="location-detect-location">
					<MyLocation onClick={this.handleDetectLocation} />
				</div>
				<div className="location-bar-wrapper">
					<SearchForm
						value={detectingLocation ? 'Loading...' : address}
						placeholder=""
						onChange={this.onChange}
						onClear={this.onClear}
						onSubmit={this.onSubmit}
					/>
				</div>
			</div>
		);
	}
}

Location.propTypes = {
	address: PropTypes.string.isRequired,
	setCoordinates: PropTypes.func.isRequired,
	onError: PropTypes.func.isRequired
};
