import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import axios from 'axios';

export default function WeatherView(props) {
	const [data, setData] = useState({});

	function handleDarkSkyCall(lat, lng) {
		return new Promise((resolve, reject) => {
			axios
				.get(
					`https://api.darksky.net/forecast/${
						process.env.REACT_APP_DARKSKYKEY
					}/${lat},${lng}`
				)
				.then(
					response => {
						const data = response.data;
						setData({ data }, () => {
							resolve();
						});
					},
					error => {
						props.onError('darksky', error);
						reject();
					}
				);
		});
	}

	function convertUnixToDate(unixTime) {
		const date = new Date(unixTime * 1000);
		return date.toLocaleDateString();
	}

	useEffect(() => {
		handleDarkSkyCall(props.lat, props.lng);
	});

	return (
		<div>
			<span>{`Currently it is ${convertUnixToDate(data.currently.time)}`}</span>
			<span>{`The temperature is ${data.currently.temperature}`}</span>
		</div>
	);
}
