import React from 'react';
import PropTypes from 'prop-types';
import './currentcard.css';

export default function CurrentCard(props) {
	const { temperature, summary, icon } = props.current;

	function fToC(temp) {
		return (temp / 32).toFixed(2);
	}

	const displayTemp =
		props.unit === 'c' ? fToC(parseFloat(temperature)) : temperature;

	return (
		<div className="current-card-set">
			<div className="current-card-wrapper">
				<div className="current-card-graphic" />
				<div className="current-card-info">
					<span className="current-card-temp">{displayTemp}&#176;</span>
					<span className="current-card-weather">{summary}</span>
				</div>
			</div>
			<div className="current-card-banner"> </div>
		</div>
	);
}

CurrentCard.propTypes = {
	current: PropTypes.shape({
		temperature: PropTypes.number.isRequired,
		summary: PropTypes.string.isRequired,
		icon: PropTypes.string.isRequired,
		humidity: PropTypes.number.isRequired,
		windSpeed: PropTypes.number.isRequired,
		uvIndex: PropTypes.number.isRequired
	}).isRequired,
	bannerItems: PropTypes.arrayOf(PropTypes.string),
	bannerNames: PropTypes.arrayOf(PropTypes.string),
	unit: PropTypes.string
};

CurrentCard.defaultProps = {
	bannerItems: ['humidity', 'windSpeed', 'uvIndex'],
	bannerNames: ['Humidity', 'WindSpeed', 'UV Index'],
	unit: 'f'
};
