import React, { Component } from 'react';
import PropTypes from 'prop-types';
import axios from 'axios';
import SearchForm from './SearchForm';
import MyLocation from '../icons/MyLocation';
import './Location.css';

export default class Location extends Component {
	constructor(props) {
		super(props);
		this.state = {
			address: props.address,
			busy: false
		};
	}

	handleGeoCodeCall = url => {
		axios
			.post(`${url}&key=${process.env.REACT_APP_GOOGLEAPIKEY}`)
			.then(response => {
				if (response.data.status === 'OK') {
					const data = response.data.results[0];
					this.setState(
						{ address: data.formatted_address, busy: false },
						() => {
							this.props.setCoordinates(data.geometry.location);
						}
					);
				} else {
					this.props.onError('geocode', response.data);
					return;
				}
			})
			.catch(error => {
				this.props.onError('geocode', error);
			});
	};

	handleIPStackCall = () => {
		axios
			.get(
				`http://api.ipstack.com/check?access_key=${
					process.env.REACT_APP_IPSTACKKEY
				}`
			)
			.then(response => {})
			.catch(error => {
				console.log(error);
			});
	};

	handleDetectLocation = () => {
		if (this.state.busy) return;
		// if (!navigator.geolocation) this.props.onError('missing features', null);
		// else {
		// 	const fallback = this.state.address;
		// 	this.setState({ busy: true }, () => {
		// 		navigator.geolocation.getCurrentPosition(
		// 			this.setCurrentPosition,
		// 			error => {
		// 				this.handleFailedDetection(fallback, error);
		// 			}
		// 		);
		// 	});
		// }
	};

	handleFailedDetection = (fallback, error) => {
		this.setState({ address: fallback, busy: false }, () => {
			this.props.onError('geolocate', error);
		});
	};

	setCurrentPosition = position => {
		const coordinates = {
			lat: position.coords.latitude,
			lng: position.coords.longitude
		};
		this.handleGeoCodeCall(
			`https://maps.googleapis.com/maps/api/geocode/json?latlng=${
				coordinates.lat
			},${coordinates.lng}`
		);
	};

	onChange = e => {
		if (this.state.busy) return;
		this.setState({ address: e.target.value });
	};

	onClear = e => {
		e.preventDefault();
		if (this.state.busy) return;
		this.setState({ address: '' });
	};

	onSubmit = e => {
		e.preventDefault();
		if (this.state.busy) return;
		this.setState({ busy: true }, () => {
			this.handleGeoCodeCall(
				`https://maps.googleapis.com/maps/api/geocode/json?address=${
					this.state.address
				}`
			);
		});
	};

	render() {
		const { address, busy } = this.state;
		return (
			<div className="location-form-wrapper">
				<div className="location-detect-location">
					<MyLocation
						onClick={this.handleDetectLocation}
						aria-label="Detect My Location"
					/>
				</div>
				<div className="location-bar-wrapper" aria-busy={busy}>
					<SearchForm
						value={busy ? 'Loading...' : address}
						placeholder=""
						onChange={this.onChange}
						onClear={this.onClear}
						onSubmit={this.onSubmit}
					/>
				</div>
			</div>
		);
	}
}

Location.propTypes = {
	address: PropTypes.string.isRequired,
	setCoordinates: PropTypes.func.isRequired,
	onError: PropTypes.func.isRequired
};
